import React, { useState } from 'react';
import propTypes from 'prop-types'



export default function Login({setToken}) {
    
    const [errorMessages, setErrorMessages] = useState({});
    const [isSubmitted, setIsSubmitted] = useState(false);

    // Generate JSX code for error message
    const renderErrorMessage = (name) =>
    name === errorMessages.name && (
    <div className="error">{errorMessages.message}</div>
    );
    // User Login info
    const database = [
        {
        username: "user1@hello.com",
        password: "pass1"
        },
        {
        username: "user2@hello.com",
        password: "pass2"
        }
    ];
    
    const errors = {
        uname: "invalid username",
        pass: "invalid password"
    };
    
    const handleSubmit = (event) => {
        //Prevent page reload
        event.preventDefault();
    
        var { uname, pword } = document.forms[0];
    
        // Find user login info
        const userData = database.find((user) => user.username === uname.value);
    
        // Compare user info
        if (userData) {
        if (userData.password !== pword.value) {
            // Invalid password
            setErrorMessages({ name: "pass", message: errors.pword });
        } else {
            setIsSubmitted(true);
        }
        } else {
        // Username not found
        setErrorMessages({ name: "uname", message: errors.uname });
        }
    };
        
        
    return(
        <div>
            <h1>Log in!</h1>
        <form className='login-wrapper' onSubmit={handleSubmit}>
        <label className='form-group'>
            <p>User Email</p>
            <input type="email" className="uname" placeholder='Email...' required/>
            {renderErrorMessage("uname")}
        </label>
        <label className='form-group'>
            <p>Password</p>
            <input type="password" className="pword" placeholder='Password...' required
            />{renderErrorMessage("pword")}
        </label>
        <div>
            <button className='Button'>Login</button>
        </div>
        </form>
        </div>
    )
}
