import axios from "axios";
import React, {useState, renderForm} from "react"
import { Link, redirect } from "react-router-dom"


export default function SignIn (){

    //React States
const [errorMessages, setErrorMessages] = useState({});
const [isSubmitted, setIsSubmitted] = useState(false);
}

const database =[
    {
        username: "user1@me.com",
        password: "pass1"
    },
    {username: "user2@me.com",
    password: "pass1"}
];

const errors = {
    email: "invalid username",
    pword: "invalid password"
};


const handleSubmit = (event) => {
    //Prevent page reload
    event.preventDefault();

    var { email, pword } = document.forms[0];

    // Find user login info
    const userData = database.find((user) => user.username === email.value);

    // Compare user info
    if (userData) {      
        if (userData.password !== pword.value) {
        // Invalid password
        setErrorMessages({ name: "pword", message: errors.email });
    } else {
        setIsSubmitted(true);
    }
    } else {
      // Username not found
        setErrorMessages({ name: "email", message: errors.email });
    }
    };
    
    const renderErrorMessage =(name) =>{
        name === errorMessages.name &&(
            <div className="error">{errorMessages.message}</div>
        );

    
    const renderForm = (
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <label>User Email</label>
                <input required type="email" name="email" className="form-control" placeholder="Email address..." />
                {renderErrorMessage("email")}
            </div>
            <div className="form-group">
                <label>Password</label>
                <input required type="password" name="pword" className="form-control" placeholder="Password..." />
                {renderErrorMessage("pword")}
            </div>
            <br/>
            <div>
                <input className="buttonComp" type="submit"/>
            </div>
        </form>
    )

    return (
        <div className="app">
            <div className="login-form">
                <div className="title">Sign In</div>
                {isSubmitted ? <div>User is successfully logged in</div> : renderForm}
             </div>
        </div>
      );
    }
