import axios from "axios";
import React, {Component} from "react"
import { Link, redirect } from "react-router-dom"

const [errorMessages, setErrorMessages] = useState({});
const [isSubmitted, setIsSubmitted] = useState(false);

const renderErrorMessage =(name) =>{
    name === errorMessages.name &&(
        <div className="error">{errorMessages.message}</div>
    );
}

const database =[
    {
        username: "user1@me.com",
        password: "pass1"
    },
    {username: "user2@me.com",
    password: "pass1"}
];

const errors = {
    uname: "invalid username",
    pass: "invalid password"
};

export default class SignIn extends Component{

    handleSubmit =e=>{
        e.preventDefault();
}
    
    render(){
        if(this.state.loggedIn){
            return <redirect to={"/"}></redirect>;
        }
    return (
        <form onSubmit={this.handleSubmit}>
            <h1>Sign in</h1>
            <div className="form-group">
                <label>User Email</label>
                <input required type="email" name="email" className="form-control" placeholder="Email address..." />
                {renderErrorMessage("email")}
            </div>
            <div className="form-group">
                <label>Password</label>
                <input required type="password" name="pword" className="form-control" placeholder="Password..." />
                {renderErrorMessage("pword")}
            </div>
            <br/>
            <div className="Button">
                <input type="submit" />
            </div>
        </form>
    )
    }
}
