import axios from "axios";
import React, {Component} from "react"
import { Link, redirect } from "react-router-dom"


export default function SignIn (){

const [errorMessages, setErrorMessages] = useState({});
const [isSubmitted, setIsSubmitted] = useState(false);

const renderErrorMessage =(name) =>{
    name === errorMessages.name &&(
        <div className="error">{errorMessages.message}</div>
    );
}

const database =[
    {
        username: "user1@me.com",
        password: "pass1"
    },
    {username: "user2@me.com",
    password: "pass1"}
];

const errors = {
    uname: "invalid username",
    pass: "invalid password"
};


const handleSubmit = (event) => {
    //Prevent page reload
    event.preventDefault();

    var { uname, pass } = document.forms[0];

    // Find user login info
    const userData = database.find((user) => user.username === uname.value);

    // Compare user info
    if (userData) {      
        if (userData.password !== pass.value) {
        // Invalid password
        setErrorMessages({ name: "pass", message: errors.pass });
    } else {
        setIsSubmitted(true);
    }
    } else {
      // Username not found
        setErrorMessages({ name: "uname", message: errors.uname });
    }
    };
    
    return (
        <form onSubmit={this.handleSubmit}>
            <div className="login-form">
            <div className="title">Sign In</div>
            {isSubmitted ? <div>User is successfully logged in</div> : renderForm}
            </div>
            <div className="form-group">
                <label>User Email</label>
                <input required type="email" name="email" className="form-control" placeholder="Email address..." />
                {renderErrorMessage("email")}
            </div>
            <div className="form-group">
                <label>Password</label>
                <input required type="password" name="pword" className="form-control" placeholder="Password..." />
                {renderErrorMessage("pword")}
            </div>
            <br/>
            <div className="Button">
                <input type="submit" />
            </div>
        </form>
    )
    }
